def handle_number(number_in_handle)
    expression = /[0-9]+/
    nb = 0
    number_in_handle.each do |element|
        if element =~ expression
            nb += 1
        end
    end
    return nb
end
puts "Il y a #{handle_number(my_array)} journaliste contenant un nombre dans le tableau"
puts "======================================================"

def contain_Aude(name)
    nb = 0
     name.each do |element|
        if element.include? ("aude")
            nb += 1
        end
    end
    return nb
end
puts "Dans cette array,  il y a #{contain_Aude(my_array)} handle contenant les 4 lettres du prénom Aude sans tenir compte des majuscules "
puts "======================================================"

def count_journaliste_capitalize(capitalize)
    nb = 0
    capitalize.each do |element|
        if element[1] == element[1].capitalize
            nb += 1
        end
    end
    return nb
end
puts " il y a #{count_journaliste_capitalize(my_array)} qui commencent par une majuscule (première lettre juste après le @)"
puts "======================================================"
def contain_maj(tableau)
    expression = /[A-Z]+/
    nb = 0
    tableau.each do |element|
        if element =~ expression
            nb += 1
        end
    end
    return nb
end
puts "Il y a #{contain_maj(my_array)} journaliste contenant un majuscule dans le tableau"
puts "================================================================="
def coutain_underscore(a)
        nb = 0
     a.each do |element|
        if element.include? ("_")
            nb += 1
        end
    end
    return nb
end
